function getDrink(n){var e="http://www.thecocktaildb.com/api/json/v1/1/lookup.php"+n;return $.ajax({url:e,async:!1,success:function(n){},error:function(n,e,r){alert("n√•got gick fel med API-anslutningen")}}).responseJSON}function renderData(n){var e=n.drinks[0];$("title").append(e.strDrink),$("#category").append(e.strCategory),$("#alcoholic").append(e.strAlcoholic),$("#name").append(e.strDrink),$("#desc").append(e.strInstructions),$("#thumb").append("<img id='pic' src='"+e.strDrinkThumb+"'>");for(var r=fixIngredients(n),t=0;t<r.length;t++){var i="<li>"+r[t]+"</li>";$("#ingredients").append(i)}}function fixIngredients(n){for(var e=JSON.stringify(n),r=[],t=1;t<15;t++){var i=e.substring(e.indexOf("strMeasure"+t),e.indexOf("strMeasure"+(t+1)));i=i.substring(i.indexOf(":")+2,i.lastIndexOf(",")-1);var s=e.substring(e.indexOf("strIngredient"+t),e.indexOf("strIngredient"+(t+1))),a=i+(s=s.substring(s.indexOf(":")+2,s.lastIndexOf(",")-1));if(" "===a)break;r.push(a)}return r}function replaceAll(n,e,r){return n.replace(new RegExp(e,"g"),r)}$(document).ready(function(){var n=window.location.href;renderData(getDrink(n.substring(n.indexOf("?"),n.length)))});